// ============================================================================
// HELPER FUNCTION - Get icon by name
// ============================================================================

/**
 * Get a tech icon component by name (case-insensitive)
 * @param name - The name of the technology
 * @returns The icon component or null if not found
 */
export function getTechIcon(name: string): React.FC<IconProps> | null {
  if (!name) return null
  const lower = name.toLowerCase().trim()
  return TECH_ICONS[lower] || null
}

/**
 * Check if a tech icon exists for the given name
 * @param name - The name of the technology
 * @returns True if an icon exists
 */
export function hasTechIcon(name: string): boolean {
  if (!name) return false
  return TECH_ICONS[name.toLowerCase().trim()] !== undefined
}

/**
 * Get all available tech icon names
 * @returns Array of all tech icon names
 */
export function getAllTechIconNames(): string[] {
  return Object.keys(TECH_ICONS)
}
